package com.biz.be03;

public class Be_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 숫자를 담을 수 있는 2개의 변수를 선언
		// 숫자 = 정수
		int intNum1 = 0; // intNum1이라는 정수형 변수를 만들고
						// 변수를 0으로 clear(초기화) 시켜라
		int intNum2 = 0; // intNum2라는 정수형 변수를 만들고
						// 변수를 0으로 clear 시켜라
		
		// 두 개의 선언된 변수에 각각 20과 30의 값을 할당하라
		intNum1 = 20; //값할당, 값저장, 값보관
		intNum2 = 30;
		
		// 두개의 변수에 할당된 값을 + 연산하여 
		// 다른 변수에 할당, 저장하라
		int intSum = intNum1 + intNum2;
		
		// 그 결과를 Console에 보여라
		System.out.println(intSum);
		
		
	}

}
