package com.biz.Be04;

public class Be_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 정수형 숫자 변수 선언
		int intNum ;
		
		// 선언된 변수는 사용만 할 수 있다. 다른 데이터타입으로라도 선언은 불가능.
		
		intNum = 10; // 1. 값 할당, 변수에 어떤 값을 보관
		System.out.println(intNum); // 2. 변수에 저장된 값을 출력
		int intNum1 = intNum ; // 3. 다른 변수에 값을 복사하는 일
		
		// 실수형 숫자 변수 선언
		float foNum ; // 선언문
		
		//문자열형 변수 선언
		String strName ;
		strName = "홍길동";
		System.out.println(strName);
		String strName1 = strName ;
		
		
		
	}

}
