package com.biz.Be04;

public class Be_06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int intNum1 = 6;
		
		// 중첩 if문
		// 비교할 조건이 여러개 일때
		if(intNum1==1) {
			System.out.println("1 입니다.");
		} else if(intNum1==2) {
			System.out.println("2 입니다.");
		} else if(intNum1==3) {
			System.out.println("3 입니다.");
		} else {
			System.out.println("1,2,3 중의 값이 아닙니다.");
		}
		
	}

}
