package com.biz.be_05;

public class Be_07 {

	/*
	 * 화면표시() 메서드를 만들고
	 * main()에서 호풀하면
	 * 
	 * 반갑습니다.
	 * 안녕하세요
	 * 또 만납시다.
	 * 라는 문자열을 콘솔에 표시
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		화면표시();
	}
	public static void 화면표시() {
		System.out.println("반갑습니다.");
		System.out.println("안녕하세요.");
		System.out.println("또 만납시다.");
	}

}
