package com.biz.be_06;

public class Be_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String strNation = "Republic of Korea";
		// String.substring() : 문자열 나누기
		// String.length() : 문자열 길기
		// String.equals() : 문자열 비교
		// String.equalsIgnoreCase() : 대소문자 없이 비교
		// String.toUpperCase() : 대문자로
		// String.toLowerCase() : 소문자로
	
		// charAt() 반대개념
		// String.indexOf("글자") String 문자열 내에서
		//						글자가 몇번째 위치에 있나?
		System.out.println(strNation.indexOf("K"));
		System.out.println(strNation.indexOf("of"));
		
		
		
	}

}
