package com.biz.hello;

public class Hello_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// println()의 괄호안에 넣을 수(포함) 할 수 있는 것들
		// 숫자
		System.out.println(3); // 3을 화면(console)에 보여달라
		
		//문자열
		System.out.println("eee"); // 그대로 보여라
		
		//숫자 계산식
		System.out.println(3+3); // 숫자 계산 결과를 보여라
		
		System.out.println("문자열 대한과 민국의 덧셈 결과");
		// 문자열들의 덧셈(+)
		System.out.println("대한"+"민국");
		// 문자열들을 덧셈하면 그냥 연결해서 한문장으로 보여라
		
		// 문자열하고 숫자를 덧셈(+)
		System.out.println("홍길동 나이는 "+30+"세 입니다.");
		System.out.println("홍길동 나이는 "+"30"+"세 입니다.");
		// 문자열과 숫자를 덧셈하면 숫자를 문자열로 바꾸어서
		// 문자열들의 덧셈처럼 취급한다.
		
		System.out.println("3+3="+(3+3));
		
		System.out.println("구구단");
		System.out.println("==========");
		
		// 6*1= 이라고 보여라
		System.out.println("6 "+"* "+"1 "+"= "+6*1);
		
		System.out.println();
		System.out.println("6*1="+( 6-1));
		System.out.println("6*2="+6*2);
		System.out.println("6*3="+6*3);
		System.out.println("6*4="+6*4);
		System.out.println("6*5="+6*5);
		System.out.println("6*6="+6*6);
		System.out.println("6*7="+6*7);
		System.out.println("6*8="+6*8);
		System.out.println("6*9="+6*9);
		
		

	}

}
